<?php

namespace Bank\tests;

use Bank\Actions\Main;

class TestSign extends BasicTest {

    /**
     *
     * @throws \ErrorException
     * Author: DQ
     */
    public function testGetSign() {
        $lib  = new Main($this->_config);
        $sn   = '20201215170123001';
        $time = '20201215170123';

        $template = sprintf('<xml version="1.0" encoding="GBK"><BOSEBankData><opReq><serialNo>%s</serialNo><reqTime>%s</reqTime><ReqParam><userID>%s</userID><userPWD>%s</userPWD></ReqParam></opReq></BOSEBankData></xml>', $sn, $time, $this->_config['userID'], $this->_config['userPWD']);

        $sign = $lib->getSign($template);

        $sameSign = '';

        $this->assertEquals($sameSign, $sign, 'Actions/Main getSign error');

    }


}